import React, { Component } from "react";
import "../App.css";
import firebase from 'firebase';

class User extends Component {
    constructor(props) {
        super(props)
        const user = firebase.auth().currentUser.displayName;
        this.state = {
            username: user
        }
    }

    render() {
        const { user } = this.state
        return (
            <div className='userDiv'>
                <p>{user}</p>
            </div>
        )
    }
}

export default User
